def isPrime(n):
    n = int(n)
    if n>1:
        for i in range(2,n):
            if n%i==0:
                return False
        return True
 
def getSum(n):
 
    sum = 0
    while (n != 0):
        sum = sum + n % 10
        n = n / 10
    return sum

def isAdditivePrime(n):
    if (not isPrime(n)):
        return False
    return isPrime(getSum(n))
    
